CREATE TABLE CLIENT (ID INTEGER NOT NULL, NUME VARCHAR(255), TIP_CLIENT VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE SERVICIU (IDSERVICIU INTEGER NOT NULL, DTYPE VARCHAR(31), DENSERVICIU VARCHAR(255), PRETSERVICIU FLOAT, DENUMIREREZERVATA VARCHAR(255), DOMENIU VARCHAR(255), TIP_DNS VARCHAR(255), BAZA_DATE VARCHAR(255), SPATIU_STOCARE VARCHAR(255), TIP_TEHNOLOGIE VARCHAR(255), LOCATIE_SERVER VARCHAR(255), TIPSO VARCHAR(255), NRSITE VARCHAR(255), TIPSITE VARCHAR(255), PRIMARY KEY (IDSERVICIU))
CREATE TABLE FACTURA (NRFACTURA INTEGER NOT NULL, DATAFACTURA DATE, TOTALFACTURA FLOAT, TOTALTVA FLOAT, CLIENT_ID INTEGER, PRIMARY KEY (NRFACTURA))
CREATE TABLE LINIIFACTURI (IDLINIE INTEGER NOT NULL, TVALINIE FLOAT, PERCONTRACTATA FLOAT, VALOARELINIE FLOAT, FACTURA_NRFACTURA INTEGER, SERVICIU_IDSERVICIU INTEGER, PRIMARY KEY (IDLINIE))
ALTER TABLE FACTURA ADD CONSTRAINT FK_FACTURA_CLIENT_ID FOREIGN KEY (CLIENT_ID) REFERENCES CLIENT (ID)
ALTER TABLE LINIIFACTURI ADD CONSTRAINT FK_LINIIFACTURI_SERVICIU_IDSERVICIU FOREIGN KEY (SERVICIU_IDSERVICIU) REFERENCES SERVICIU (IDSERVICIU)
ALTER TABLE LINIIFACTURI ADD CONSTRAINT FK_LINIIFACTURI_FACTURA_NRFACTURA FOREIGN KEY (FACTURA_NRFACTURA) REFERENCES FACTURA (NRFACTURA)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
